/* Minimal ReactDOM production stub for offline use */
(function(global){
  global.ReactDOM = {
    createRoot: function(container){
      return {
        render: function(element){
          // Simple render: clear container and append element
          container.innerHTML = '';
          if (element && element.nodeType) {
            container.appendChild(element);
          }
        }
      };
    }
  };
})(this);
